import React, { useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import { useDispatch, useSelector } from 'react-redux';
import { fetchHeroDetailsStart, fetchHeroDetailsSuccess, fetchHeroDetailsError } from '../store/heroActions';
import axios from 'axios';

const Hero = () => {
  const { id } = useParams();
  const navigate = useNavigate();
  const dispatch = useDispatch();

  const { hero, films, loading, error } = useSelector((state) => state.hero);

  useEffect(() => {
    const fetchHeroDetails = async () => {
      dispatch(fetchHeroDetailsStart(id));

      try {
        const response = await axios.get(`https://swapi.dev/api/people/${id}/`);
        const heroData = response.data;

        const filmData = await Promise.all(heroData.films.map(url => axios.get(url).then(res => res.data)));

        dispatch(fetchHeroDetailsSuccess({ hero: heroData, films: filmData }));
      } catch (error) {
        dispatch(fetchHeroDetailsError(error));
      }
    };

    fetchHeroDetails();
  }, [dispatch, id]);

  const handleFilmClick = (id) => {
    navigate(`/about-film/${id}`);
  };

  if (loading) {
    return <div>Loading...</div>;
  }

  if (error) {
    return <div>Error: {error.message}</div>;
  }


  return (
    <div className='box'>
      <div className='character'>
        {/* Використовуйте дані з Redux замість локального стану */}
        <h1 id="heroName">{hero && hero.name}</h1>
        <div className="details">
          <p><span className="label">Height:</span> {hero.height}</p>
          <p><span className="label">Mass:</span> {hero.mass}</p>
          <p><span className="label">Hair color:</span> {hero.hair_color}</p>
          <p><span className="label">Skin color:</span> {hero.skin_color}</p>
          <p><span className="label">Eyes color:</span> {hero.eye_color}</p>
          <p><span className="label">Birth Year:</span> {hero.birth_year}</p>
          <p><span className="label">Gender:</span> {hero.gender}</p>
        </div>
        <div className="filmInclude">
          <h2 id="labelFilm">Films:</h2>
          {films.map((film, index) => (
            <div className="AllFilms" key={index} onClick={() => handleFilmClick(film.episode_id)}>
              <p>{film.title}</p>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
};

export default Hero;
