import React, { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import { useDispatch, useSelector } from 'react-redux';
import {
  fetchHeroDetails,
  setSelectedFilm,
} from '../store/heroReducer';
import axios from 'axios';

const Hero = () => {
  const { id } = useParams();
  const navigate = useNavigate();
  const dispatch = useDispatch();

  const { hero, films, loading, error } = useSelector((state) => state.hero);

  useEffect(() => {
    dispatch(fetchHeroDetails(id));
  }, [id, dispatch]);

  const handleFilmClick = (film) => {
    dispatch(setSelectedFilm(film));
    navigate(`/about-film/${film.id}`); // Assuming 'id' property exists in films
  };

  if (loading) {
    return <div>Завантаження...</div>;
  }

  if (error) {
    return <div>Помилка: {error}</div>;
  }

  return (
    <div className="box">
      <div className="character">
        <h1 id="heroName">{hero.name}</h1>
        <div className="details">
          {/* Деталі з hero */}
        </div>
        <div className="filmInclude">
          <h2>Фільми:</h2>
          {films.map((film, index) => (
            <div className="AllFilms" key={index} onClick={() => handleFilmClick(film)}>
              <p>{film.title}</p>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
};

export default Hero;
