import React from 'react';

class AboutFilm extends React.Component {
  render() {
    // Перевірка, чи існує this.props.location.state перед читанням
    const film = this.props.location.state ? this.props.location.state.film : null;

    return (
      <div>
        {film ? (
          <div>
            <h1>About Film</h1>
            <p>Episode: {film.episode_id}</p>
            <p>Title: {film.title}</p>
            <p>Director: {film.director}</p>
            <p>Release Date: {film.release_date}</p>
          </div>
        ) : (
          <p>Film information not available</p>
        )}
      </div>
    );
  }
}

export default AboutFilm;
