// AboutFilmAction.js
import axios from 'axios';

export const SET_FILM_DETAILS = 'SET_FILM_DETAILS';
export const SET_CHARACTERS = 'SET_CHARACTERS';
export const SET_PLANETS = 'SET_PLANETS';
export const SET_STARSHIPS = 'SET_STARSHIPS';
export const SET_VEHICLES = 'SET_VEHICLES';
export const SET_SPECIES = 'SET_SPECIES';

export const setFilmDetails = (filmDetails) => ({
  type: SET_FILM_DETAILS,
  payload: filmDetails,
});

export const setCharacters = (characters) => ({
  type: SET_CHARACTERS,
  payload: characters,
});

export const setPlanets = (planets) => ({
  type: SET_PLANETS,
  payload: planets,
});

export const setStarships = (starships) => ({
  type: SET_STARSHIPS,
  payload: starships,
});

export const setVehicles = (vehicles) => ({
  type: SET_VEHICLES,
  payload: vehicles,
});

export const setSpecies = (species) => ({
  type: SET_SPECIES,
  payload: species,
});

export const setLoading = (loading) => ({
  type: 'SET_LOADING',
  payload: loading,
});

export const fetchFilmDetails = (filmId) => async (dispatch) => {
  dispatch(setLoading(true));

  try {
    const response = await axios.get(`https://swapi.dev/api/films/${filmId}/`);
    const filmData = response.data;

    dispatch(setFilmDetails(filmData));
    dispatch(setCharacters(filmData.characters));
    dispatch(setPlanets(filmData.planets));
    dispatch(setStarships(filmData.starships));
    dispatch(setVehicles(filmData.vehicles));
    dispatch(setSpecies(filmData.species));
  } catch (error) {
    console.error('Error fetching film details:', error);
  } finally {
    dispatch(setLoading(false));
  }
};
