// aboutFilmActions.js
import { setFilmDetails } from './aboutFilmReducer';

const fetchFilmDetails = (id) => async (dispatch) => {
  useEffect(() => {
    const fetchData = async () => {
      try {
        dispatch(setLoading(true));

        const response = await fetch(`https://swapi.dev/api/films/${id}/`);

        if (!response.ok) {
          throw new Error(`Error fetching film details: ${response.statusText}`);
        }

        const filmData = await response.json();

        const charactersData = await Promise.all(
          filmData.characters.map(async (url) => {
            const characterResponse = await fetch(url);
            return characterResponse.json();
          })
        );

        const planetsData = await Promise.all(
          filmData.planets.map(async (url) => {
            const planetResponse = await fetch(url);
            return planetResponse.json();
          })
        );

        const starshipsData = await Promise.all(
          filmData.starships.map(async (url) => {
            const starshipResponse = await fetch(url);
            return starshipResponse.json();
          })
        );

        const vehiclesData = await Promise.all(
          filmData.vehicles.map(async (url) => {
            const vehicleResponse = await fetch(url);
            return vehicleResponse.json();
          })
        );

        const speciesData = await Promise.all(
          filmData.species.map(async (url) => {
            const speciesResponse = await fetch(url);
            return speciesResponse.json();
          })
        );

        dispatch(
          setFilmDetails({
            film: filmData,
            characters: charactersData,
            planets: planetsData,
            starships: starshipsData,
            vehicles: vehiclesData,
            species: speciesData,
          })
        );
        dispatch(setLoading(false));

      } catch (error) {
        console.error('Error fetching film details:', error);
        dispatch(setLoading(false));
      }
    };

    fetchData();
  }, [dispatch, id]);

  return null; // або виведення JSX вашого компонента
};
