// Hero.js
import React, { useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import { useDispatch, useSelector } from 'react-redux';
import { fetchHeroDetails } from './path-to-heroActions';

const Hero = () => {
  const { id } = useParams();
  const navigate = useNavigate();
  const dispatch = useDispatch();

  const { hero, films, loading, error } = useSelector((state) => state.hero);

  useEffect(() => {
    dispatch(fetchHeroDetails(id));
  }, [dispatch, id]);

  const handleFilmClick = (id) => {
    navigate(`/about-film/${id}`);
  };

  if (loading) {
    return <div>Loading...</div>;
  }

  return (
    <div className='box'>
      <div className='character'>
        <h1 id="heroName">{hero.name}</h1>
        <div className="details">
          {/* Залиште решту вашого коду для відображення характеристик героя */}
        </div>
        <div className="filmInclude">
          <h2 id="labelFilm">Films:</h2>
          {films.map((film, index) => (
            <div className="AllFilms" key={index} onClick={() => handleFilmClick(film.episode_id)}>
              <p>{film.title}</p>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
};

export default Hero;
