import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { useLocation, useNavigate } from 'react-router-dom';
import star from '../img/star.jpeg';

const API_URL = 'https://swapi.dev/api/films/'; // Replace with the correct API URL

const getFilmById = async (id) => {
  const response = await axios.get(`${API_URL}${id}/`);
  return response.data;
};

function AboutFilm() {
  const location = useLocation();
  const navigate = useNavigate();

  const filmFromLocation = location.state?.film;
  const filmIdFromUrl = filmFromLocation?.url?.split('/').slice(-2, -1)[0];
  const { id } = useParams();

  const [film, setFilm] = useState(null);
  const [characters, setCharacters] = useState([]);
  const [otherData, setOtherData] = useState([]);

  useEffect(() => {
    const fetchData = async () => {
      const filmId = filmIdFromUrl || id;
      if (filmId) {
        const filmData = await getFilmById(filmId);
        setFilm(filmData);

        const otherDataPromises = [
          fetchCharacters(filmData),
          fetchPlanets(filmData),
          fetchStarships(filmData),
          fetchVehicles(filmData),
          fetchSpecies(filmData),
        ];

        const otherData = await Promise.all(otherDataPromises);
        setOtherData(otherData);
      }
    };

    fetchData();
  }, [filmIdFromUrl, id]);

  const navigateToCharacter = (url) => {
    navigate(`/character/${url.match(/\/people\/(\d+)/)[1]}`);
  };

  const fetchCharacters = async (filmData) => {
    return await Promise.all(filmData.characters.map((url) => fetch(url).then((res) => res.json())));
  };

  const fetchPlanets = async (filmData) => {
    return await Promise.all(filmData.planets.map((url) => fetch(url).then((res) => res.json())));
  };

  const fetchStarships = async (filmData) => {
    return await Promise.all(filmData.starships.map((url) => fetch(url).then((res) => res.json())));
  };

  const fetchVehicles = async (filmData) => {
    return await Promise.all(filmData.vehicles.map((url) => fetch(url).then((res) => res.json())));
  };

  const fetchSpecies = async (filmData) => {
    return await Promise.all(filmData.species.map((url) => fetch(url).then((res) => res.json())));
  };

  if (!film) {
    return <p>No data available</p>;
  }


  return (
    <div className='block'>
      <img src={star} alt='star' className='backStar' />
      <div className='filmInfo'>
        <p id="mainTitle"><span style={{ fontSize: '24px', fontWeight: 600, paddingTop: '20px', color: 'blue' }}>{film.title}</span></p>
        <p id="mainDirector"><span style={{ fontSize: '24px', fontWeight: 600, paddingTop: '20px', color: 'blue' }}>Director:</span> {film.director}</p>
        <p id="mainReleaseDate"><span style={{ fontSize: '24px', fontWeight: 600, paddingTop: '20px', color: 'blue' }}>Release Date:</span> {film.release_date}</p>
        <p id="mainProducer"><span style={{ fontSize: '24px', fontWeight: 600, paddingTop: '20px', color: 'blue' }}>Producer:</span> {film.producer}</p>
        <p id="mainOpening"><span style={{ fontSize: '24px', fontWeight: 600, paddingTop: '20px', color: 'blue' }}>Opening:</span> {film.opening_crawl}</p>

        <div className='detailsInfo'>
          <div className='planets'>
            <p id="details" >Planets:</p>
            {planets.map((planet, filmIndex) => (
              <p key={filmIndex}>{planet.name}</p>
            ))}
          </div>

          <div className="starships">
            <p id="details">Starships:</p>
            {starships.map((starship, filmIndex) => (
              <p key={filmIndex}>{starship.name}</p>
            ))}
          </div>

          <div className="vehicles">
            <p id="details">Vehicles:</p>
            {vehicles.map((vehicle, filmIndex) => (
              <p key={filmIndex}>{vehicle.name}</p>
            ))}
          </div>

          <div className="species">
            <p id="details">Species:</p>
            {species.map((specie, filmIndex) => (
              <p key={filmIndex}>{specie.name}</p>
            ))}
          </div>

          <div className="hero">
            <p id="details">Characters:</p>
            {characters.map((character, filmIndex) => (
              <p id="focus" key={filmIndex} onClick={() => navigateToCharacter(character.url)}>{character.name}</p>
            ))}
          </div>
        </div>
      </div>
    </div>
  );
}

export default AboutFilm;
