import React, { useEffect, useState } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import axios from 'axios';

function Hero() {
  const { id } = useParams();
    const navigate = useNavigate();

  const [character, setCharacter] = useState(null);
  const [films, setFilms] = useState([]);


  useEffect(() => {
    const fetchCharacter = async () => {
      const data = await axios.get(`https://swapi.dev/api/people/${id}/`);
      setCharacter(data.data);

      const filmData = await Promise.all(data.data.films.map(film => axios.get(film)));
      setFilms(filmData.map(response => response.data));
    };

    fetchCharacter();
  }, [id]); // Запускати useEffect, коли `id` змінюється

  useEffect(() => {
    const fetchCharacter = async () => {
        const data = await axios.get(`https://swapi.dev/api/people/${id}/`);
        setCharacter(data.data);

    }
  } , [character]);

  const handleFilmClick = (id) => {
    // Використовуйте navigate для переходу на сторінку AboutFilm
    navigate(`/about-film/${id}`);
  };

  if (!character) {
    return <div>Loading...</div>;
  }

  return (
    <div className='box'>
        <div className='character'>
        <h1>{character.name}</h1>
        <p>Height: {character.height}</p>
        <p>Mass: {character.mass}</p>
        <p>Heir color: {character.hair_color}</p>
        <p>Skin color: {character.skin_color}</p>
        <p>Eyes color: {character.eye_color}</p>
        <p>Birth Year: {character.birth_year}</p>
        <p>Gender: {character.gender}</p>
        <div className = "filmInclude">
            {films.map((film, index) => (
            <div key={index}>

                <p>{film.title}</p>

            </div>
            ))}
        </div>
        </div>
    </div>
  );
}

export default Hero;
