import React from 'react';
import axios from 'axios';

const swapiUrl = 'https://swapi.dev/api/films';

class Films extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      films: []  // Зберігатимемо дані про всі фільми в масиві
    };
  }

  componentDidMount() {
    axios.get(swapiUrl)
      .then((res) => {
        const films = res.data.results;
        this.setState({ films });
      })
      .catch((error) => {
        console.error('Помилка при отриманні даних:', error);
      });
  }

  render() {
    return (
      <div className='film'>
        <h1>Фільми "Зоряні війни"</h1>

          {this.state.films.map((film, index) => (
            <div key={index}>
              <p>Епізод: {film.episode_id}</p>
              <p>Назва: {film.title}</p>
              <p>Режисер: {film.director}</p>
              <p>Дата виходу: {film.release_date}</p>
            </div>
          ))}

      </div>
    );
  }
}

export default Films;
