// store.js
import { createStore, applyMiddleware } from 'redux';
import rootReducer from './rootReduser';
import aboutFilmReducer from './aboutFilmReducer';


// Створення middleware
const simpleMiddleware = store => next => action => {
  // Перевірка, чи action є функцією
  if (typeof action === 'function') {
    return action(store.dispatch, store.getState);
  }
  // Якщо action не є функцією, продовжте далі
  return next(action);
};

const store = createStore(rootReducer, applyMiddleware(simpleMiddleware));

export default store;
