// aboutFilmReducer.js

import axios from 'axios';

// Action types
const SET_FILM = 'SET_FILM';
const SET_FILM_DETAILS = 'SET_FILM_DETAILS';

// Action creators
export const setFilm = (film) => ({
  type: SET_FILM,
  payload: film,
});

export const setFilmDetails = (filmDetails) => ({
  type: SET_FILM_DETAILS,
  payload: filmDetails,
});

// Initial state
const initialState = {
  film: null,
  filmDetails: {},
};

// Reducer
const aboutFilmReducer = (state = initialState, action) => {
  switch (action.type) {
    case SET_FILM:
      return {
        ...state,
        film: action.payload,
      };
    case SET_FILM_DETAILS:
      return {
        ...state,
        filmDetails: {
          ...state.filmDetails,
          [action.payload.id]: action.payload.details,
        },
      };
    default:
      return state;
  }
};

export default aboutFilmReducer;
