import React, { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import { useDispatch, useSelector } from 'react-redux';
import { fetchHeroDetails, setSelectedFilm } from '../storeє '; // Імпортуємо функцію fetchHeroDetails та екшн setSelectedFilm з heroReducer

const Hero = () => {
  const { id } = useParams();
  const navigate = useNavigate();
  const dispatch = useDispatch();

  const { hero, films, loading } = useSelector((state) => state.hero); // Отримуємо дані зі стору

  useEffect(() => {
    dispatch(fetchHeroDetails(id)); // Викликаємо функцію для завантаження даних героя при завантаженні компонента
  }, [dispatch, id]);

  const handleFilmClick = (film) => {
    const getFilmIndexByUrl = (url) => {
      const matches = url.match(/\/films\/(\d+)/);
      return matches ? matches[1] : null;
    }
    const filmId = getFilmIndexByUrl(film.url);
    dispatch(setSelectedFilm(film));
    navigate(`/about-film/${filmId}`);
  };

  if (loading) {
    return <div>Loading...</div>;
  }

  return (
    <div className='box'>
      <div className='character'>
        <h1 id="heroName">{hero?.name}</h1>
        <div className="details">
          <p><span className="label">Height:</span> {hero?.height}</p>
          <p><span className="label">Mass:</span> {hero?.mass}</p>
          <p><span className="label">Hair color:</span> {hero?.hair_color}</p>
          <p><span className="label">Skin color:</span> {hero?.skin_color}</p>
          <p><span className="label">Eyes color:</span> {hero?.eye_color}</p>
          <p><span className="label">Birth Year:</span> {hero?.birth_year}</p>
          <p><span className="label">Gender:</span> {hero?.gender}</p>
        </div>
        <div className="filmInclude">
          <h2 id="labelFilm">Films:</h2>
          {films.map((film, index) => (
            <div className="AllFilms" key={index} onClick={() => handleFilmClick(film)}>
              <p>{film.title}</p>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
};

export default Hero;
