// filmsReducer.js
import axios from 'axios';

// Action types
const SET_FILMS = 'SET_FILMS';

// Action creator
export const setFilms = (films) => ({
  type: SET_FILMS,
  payload: films,
});

// Initial state
const initialState = {
  films: [],
};

// Reducer
const filmsReducer = (state = initialState, action) => {
  switch (action.type) {
    case SET_FILMS:
      return {
        ...state,
        films: action.payload,
      };
    default:
      return state;
  }
};

export default filmsReducer;
