// aboutFilmReducer.js
import axios from 'axios';

const SET_FILM_DETAILS = 'SET_FILM_DETAILS';
const SET_LOADING = 'SET_LOADING';

export const setFilmDetails = (filmDetails) => ({
  type: SET_FILM_DETAILS,
  payload: filmDetails,
});

export const setLoading = (loading) => ({
  type: SET_LOADING,
  payload: loading,
});

export const fetchFilmDetails = (filmId) => async (dispatch) => {
  try {
    dispatch(setLoading(true));

    const response = await axios.get(`https://swapi.dev/api/films/${filmId}/`);
    const filmDetails = response.data;

    dispatch(setFilmDetails(filmDetails));
  } catch (error) {
    console.error('Error fetching film details:', error);
  } finally {
    dispatch(setLoading(false));
  }
};

const initialState = {
  film: null,
  characters: [],
  planets: [],
  starships: [],
  vehicles: [],
  species: [],
  loading: false,
};

const aboutFilmReducer = (state = initialState, action) => {
  switch (action.type) {
    case SET_FILM_DETAILS:
      return {
        ...state,
        film: action.payload,
        characters: action.payload.characters || [],
        planets: action.payload.planets || [],
        starships: action.payload.starships || [],
        vehicles: action.payload.vehicles || [],
        species: action.payload.species || [],
      };

    case SET_LOADING:
      return {
        ...state,
        loading: action.payload,
      };

    default:
      return state;
  }
};

export default aboutFilmReducer;
