import axios from 'axios';

const SET_ABOUT_FILM = 'SET_ABOUT_FILM';

export const setAboutFilm = (film) => ({
  type: SET_ABOUT_FILM,
  payload: film,
});

const initialState = {
  film: null,
  characters: [],
  planets: [],
  starships: [],
  vehicles: [],
  species: [],
};

export const fetchAboutFilm = (id) => async (dispatch) => {
  try {
    const response = await axios.get(`https://swapi.dev/api/films/${id}/`);
    const filmData = response.data;

    dispatch(setAboutFilm(filmData));
  } catch (error) {
    console.error('Error fetching about film:', error);
  }
};

const aboutFilmReducer = (state = initialState, action) => {
  switch (action.type) {
    case SET_ABOUT_FILM:
      return {
        ...state,
        film: action.payload,
        characters: action.payload.characters,
        planets: action.payload.planets,
        starships: action.payload.starships,
        vehicles: action.payload.vehicles,
        species: action.payload.species,
      };
    default:
      return state;
  }
};

export default aboutFilmReducer;
