import React from 'react';

class AboutFilm extends React.Component {
  constructor(props) {
    super(props);

    this.state = {
      film: null,
    };
  }

  componentDidMount() {
    const film = this.props.location.state?.film;

    // Check if the film is available before updating the state
    if (film) {
      this.setState({ film });
    }
  }

  render() {
    const { film } = this.state;

    // Check if the film is available in the state
    if (!film) {
      return <p>No data available</p>;
    }

    return (
      <div>
        <h1>About Film</h1>
        <p>Episode: {film.episode_id}</p>
        <p>Title: {film.title}</p>
        <p>Director: {film.director}</p>
        <p>Release Date: {film.release_date}</p>
      </div>
    );
  }
}

export default AboutFilm;
