import React, { useEffect, useState } from 'react';
import { useLocation } from 'react-router-dom';
import star from '../img/star.jpeg';

function AboutFilm() {
  const location = useLocation();
  const film = location.state?.film;
  const filmIndex = location.state?.filmIndex;

  const [characters, setCharacters] = useState([]);

  useEffect(() => {
    if (film) {
      const fetchCharacters = async () => {
        const promises = film.people.map((url) => fetch(url).then((res) => res.json()));
        const people = await Promise.all(promises);
        setCharacters(characters);
      };
      fetchCharacters();
    }
  }, [film]);
  console.log(people);
  if (!film) {
    return <p>No data available</p>;
  }

  return (
    <div className='block'>
      <img src={star} alt='star' className='backStar' />
      <div className='filmInfo'>
        <p>Film Index: {filmIndex}</p>
        <p>Title: {film.title}</p>
        <p>opening_crawl: {film.opening_crawl}</p>
        <p>Director: {film.director}</p>
        <p>Release Date: {film.release_date}</p>
        {people.map((character, index) => (
          <p className="hero" key={index}>{character.name}</p>
        ))}
      </div>
    </div>
  );
}

export default AboutFilm;
