import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { useLocation, useNavigate } from 'react-router-dom';
import star from '../img/star.jpeg';

const API_URL = 'https://swapi.dev/api'; // Замініть на потрібний URL API

const getFilmById = (episode_id) => {
  return axios.get(`${API_URL}/films/${+episode_id}`).then(response => {
    return response.data;
  });
};
getFilmById(1);

function AboutFilm() {
  const location = useLocation();
  const navigate = useNavigate();
  const filmFromLocation = location.state ? location.state.film : null;
  const episode_id = filmFromLocation ? filmFromLocation.episode_id : null;
  console.log(episode_id); // це буде ідентифікатор фільму


  const [film, setFilm] = useState(null);
  const [characters, setCharacters] = useState([]);
  const [planets, setPlanets] = useState([]);
  const [starships, setStarships] = useState([]);
  const [vehicles, setVehicles] = useState([]);
  const [species, setSpecies] = useState([]);

  const [urlFilm, setUrlFilm] = useState(null);

  useEffect(() => {
    if (episode_id) {
      getFilmById(episode_id).then((data) => setFilm(data));
    }
  }, [episode_id]);

  const navigateToCharacter = (url) => {
    const match = url.match(/\/people\/(\d+)/);
    if (match) {
      const id = match[1];
      navigate(`/character/${id}`);
    }
  };

  useEffect(() => {
    if (filmFromLocation) {
      const fetchCharacters = async () => {
        const promises = filmFromLocation.characters.map((url) => fetch(url).then((res) => res.json()));
        const characters = await Promise.all(promises);
        setCharacters(characters);
      };
      fetchCharacters();
    }
  }, [filmFromLocation]);

  useEffect(() => {
    if (filmFromLocation) {
      const fetchPlanets = async () => {
        const promises = filmFromLocation.planets.map((url) => fetch(url).then((res) => res.json()));
        const planets = await Promise.all(promises);
        setPlanets(planets);
      };
      fetchPlanets();
    }
  }, [filmFromLocation]);

  useEffect(() => {
    if (filmFromLocation) {
      const fetchStarships = async () => {
        const promises = filmFromLocation.starships.map((url) => fetch(url).then((res) => res.json()));
        const starships = await Promise.all(promises);
        setStarships(starships);
      };
      fetchStarships();
    }
  }, [filmFromLocation]);

  useEffect(() => {
    if (filmFromLocation) {
      const fetchVehicles = async () => {
        const promises = filmFromLocation.vehicles.map((url) => fetch(url).then((res) => res.json()));
        const vehicles = await Promise.all(promises);
        setVehicles(vehicles);
      };
      fetchVehicles();
    }
  }, [filmFromLocation]);

  //!
  useEffect(() =>{
    if (filmFromLocation){
      const fetchUrlFilm = async () => {
        const promises = filmFromLocation.url.map((url) => fetch(url).then((res) => res.json()));
        const urlFilm = await Promise.all(promises);
        setUrlFilm(urlFilm);
      }
    }
  })


  useEffect(() => {
    const fetchFilm = async () => {
      try {
        const filmData = await getFilmById(episode_id); // Використовуємо функцію для отримання інформації про фільм
        setFilm(filmData);
      } catch (error) {
        console.error('Error fetching film:', error);
      }
    };

    fetchFilm();
  }, [episode_id]);


  if (!film) {
    return <p>No data available</p>;
  }

  return (
    <div className='block'>
      <img src={star} alt='star' className='backStar' />
      <div className='filmInfo'>
        <p id="mainTitle"><span style={{ fontSize: '24px', fontWeight: 600, paddingTop: '20px', color: 'blue' }}>{film.title}</span></p>
        <p id="mainDirector"><span style={{ fontSize: '24px', fontWeight: 600, paddingTop: '20px', color: 'blue' }}>Director:</span> {film.director}</p>
        <p id="mainReleaseDate"><span style={{ fontSize: '24px', fontWeight: 600, paddingTop: '20px', color: 'blue' }}>Release Date:</span> {film.release_date}</p>
        <p id="mainProducer"><span style={{ fontSize: '24px', fontWeight: 600, paddingTop: '20px', color: 'blue' }}>Producer:</span> {film.producer}</p>
        <p id="mainOpening"><span style={{ fontSize: '24px', fontWeight: 600, paddingTop: '20px', color: 'blue' }}>Opening:</span> {film.opening_crawl}</p>
        <p>{}</p>
        <div className='detailsInfo'>
          <div className='planets'>
            <p id="details" >Planets:</p>
            {planets.map((planet, filmIndex) => (
              <p key={filmIndex}>{planet.name}</p>
            ))}
          </div>

          <div className="starships">
            <p id="details">Starships:</p>
            {starships.map((starship, filmIndex) => (
              <p key={filmIndex}>{starship.name}</p>
            ))}
          </div>

          <div className="vehicles">
            <p id="details">Vehicles:</p>
            {vehicles.map((vehicle, filmIndex) => (
              <p key={filmIndex}>{vehicle.name}</p>
            ))}
          </div>

          <div className="species">
            <p id="details">Species:</p>
            {species.map((specie, filmIndex) => (
              <p key={filmIndex}>{specie.name}</p>
            ))}
          </div>

          <div className="hero">
            <p id="details">Characters:</p>
            {characters.map((character, filmIndex) => (
              <p id="focus" key={filmIndex} onClick={() => navigateToCharacter(character.url)}>{character.name}</p>
            ))}
          </div>
        </div>
      </div>
    </div>
  );
}

export default AboutFilm;
