import React, { useEffect, useState } from 'react';
import { useLocation } from 'react-router-dom';
import star from '../img/star.jpeg';

function AboutFilm() {
  const location = useLocation();
  const film = location.state?.film;
  const filmIndex = location.state?.filmIndex;


  const [characters, setCharacters] = useState([]);
  const [planets, setPlanets] = useState([]);
  const [starships, setStarships] = useState([]);

  useEffect(() => {
    if (film) {
      const fetchCharacters = async () => {
        const promises = film.characters.map((url) => fetch(url).then((res) => res.json()));
        const characters = await Promise.all(promises);
        setCharacters(characters);
      };
      fetchCharacters();
    }
  }, [film]);

  useEffect(() => {
    if (film) {
      const fetchPlanets = async () => {
        const promises = film.planets.map((url) => fetch(url).then((res) => res.json()));
        const planets = await Promise.all(promises);
        setPlanets(planets);
      };
      fetchPlanets();
    }
  }, [film]);

  useEffect(() => {
    if (film) {
      const fetchStarships = async () => {
        const promises = film.starships.map((url) => fetch(url).then((res) => res.json()));
        const starships = await Promise.all(promises);
        setStarships(starships);
      };
      fetchStarships();
    }
  }, [film]);

  console.log(characters);
  if (!film) {
    return <p>No data available</p>;
  }

  return (
    <div className='block'>
      <img src={star} alt='star' className='backStar' />
      <div className='filmInfo'>
        <p>Film Index: {filmIndex}</p>
        <p>Title: {film.title}</p>
        <p>opening_crawl: {film.opening_crawl}</p>
        <p>Director: {film.director}</p>
        <p>Release Date: {film.release_date}</p>

        <div className='planets'>
          <p id = "Planets">Planets:</p>
          {planets.map((planet, filmIndex) => (
            <p key={filmIndex}>{planet.name}</p>
          ))}
        </div>


        <div className="hero">
          <p id = "Hero">Characters:</p>
          {characters.map((character, filmIndex) => (
            <p key={filmIndex}>{character.name}</p>
          ))}
        </div>

      </div>
    </div>
  );
}

export default AboutFilm;
