import React from 'react';
import axios from 'axios';

const swapiUrl = 'https://swapi.dev/api/films';

class Films extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      episode: '',
      directorsName: '',
      releaseDate: ''
    };
  }

  componentDidMount() {
    axios.get(swapiUrl)
      .then((res) => {
        const film = res.data.results[0]; // Припускається, що ви хочете отримати дані для першого фільму
        this.setState({
          episode: film.title,
          directorsName: film.director,
          releaseDate: film.release_date
        });
      })
      .catch((error) => {
        console.error('Помилка при отриманні даних:', error);
      });
  }

  render() {
    return (
      <div className='film'>
        <h1>Фільми "Зоряні війни"</h1>
        <p>Епізод: {this.state.episode}</p>
        <p>Режисер: {this.state.directorsName}</p>
        <p>Дата виходу: {this.state.releaseDate}</p>
      </div>
    );
  }
}

export default Films;
