import React from 'react';
import axios from 'axios';
import { Link } from 'react-router-dom';
import space from '../img/space.jpeg';


const swapiUrl = 'https://swapi.dev/api/films';

class Films extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      films: []  // Зберігатимемо дані про всі фільми в масиві
    };
  }

  componentDidMount() {
    axios.get(swapiUrl)
      .then((res) => {
        const films = res.data.results;
        this.setState({ films });
      })
      .catch((error) => {
        console.error('Помилка при отриманні даних:', error);
      });
  }

  render() {
    console.log(this.state.films);

    return (
        <div className="container">
          <img src={space} alt='space' className='backSpace' />
          <div className="content">
            <div className='filmsInfo'>
              {this.state.films.map((film, index) => (
                <Link
                  key={index}
                  to={{ pathname: '/about-film', state: { film } }}
                  className='filmsAbout'>
                  <p id="title">{film.title}</p>
                  <p id="director"> {film.director}</p>
                  <p id="release"> {film.release_date}</p>
                </Link>
              ))}
            </div>
          </div>
        </div>
      );

  }
}

export default Films;
