import React from 'react';
import axios from 'axios';
import { Link } from 'react-router-dom';
import '..css/style.css';

const swapiUrl = 'https://swapi.dev/api/films';

class Films extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      films: []  // Зберігатимемо дані про всі фільми в масиві
    };
  }

  componentDidMount() {
    axios.get(swapiUrl)
      .then((res) => {
        const films = res.data.results;
        this.setState({ films });
      })
      .catch((error) => {
        console.error('Помилка при отриманні даних:', error);
      });
  }

  render() {
    console.log(this.state.films);

    return (
        <div>
          <h1 id = "text-title">Фільми "Зоряні війни"</h1>
          <div className='filmsInfo'>
            {this.state.films.map((film, index) => (
              <div key={index} className='filmsAbout'>
                <p>Episode: {film.episode_id}</p>
                <p>Title: {film.title}</p>
                <p>Director: {film.director}</p>
                <p>Release Date: {film.release_date}</p>
                <Link to={{ pathname: '/about-film', state: { film } }}>ABOUT FILM</Link>
              </div>
            ))}
          </div>
        </div>
      );

  }
}

export default Films;
