// store/index.js
import { combineReducers } from 'redux';
import filmsReducer from './filmsReducer';
import aboutFilmReducer from './aboutFilmReducer'; // Змінено імпорт

const rootReducer = combineReducers({
  films: filmsReducer,
  aboutFilm: aboutFilmReducer, // Змінено ключ

});

export default rootReducer;
