import { setFilmDetails, setLoading } from './aboutFilmReducer';
import axios from 'axios';


const API_URL = 'https://swapi.dev/api/films/';
filmIdFromUrl = window.location.pathname.split('/')[2];
export const fetchFilmDetails = (id) => async (dispatch) => {
  try {
    dispatch(setLoading(true));

    const response = await axios.get(`${API_URL}${id || filmIdFromUrl}/`);
    const filmData = response.data;

    const charactersPromises = filmData.characters.map((url) => axios.get(url).then((response) => response.data));
    const planetsPromises = filmData.planets.map((url) => axios.get(url).then((response) => response.data));
    const starshipsPromises = filmData.starships.map((url) => axios.get(url).then((response) => response.data));
    const vehiclesPromises = filmData.vehicles.map((url) => axios.get(url).then((response) => response.data));
    const speciesPromises = filmData.species.map((url) => axios.get(url).then((response) => response.data));

    const [charactersData, planetsData, starshipsData, vehiclesData, speciesData] = await Promise.all([
      Promise.all(charactersPromises),
      Promise.all(planetsPromises),
      Promise.all(starshipsPromises),
      Promise.all(vehiclesPromises),
      Promise.all(speciesPromises),
    ]);

    dispatch(
      setFilmDetails({
        film: filmData,
        characters: charactersData,
        planets: planetsData,
        starships: starshipsData,
        vehicles: vehiclesData,
        species: speciesData,
      })
    );

    dispatch(setLoading(false));
  } catch (error) {
    console.error('Error fetching film details:', error.message);
    dispatch(setLoading(false));
  }
};
