import React, { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import axios from 'axios';

const Hero = () => {
  const { id } = useParams();
  const navigate = useNavigate();

  const [character, setCharacter] = useState(null);
  const [films, setFilms] = useState([]);

  useEffect(() => {
    const fetchCharacter = async () => {
      const data = await axios.get(`https://swapi.dev/api/people/${id}/`);
      setCharacter(data.data);

      const filmData = await Promise.all(data.data.films.map(film => axios.get(film)));
      setFilms(filmData.map(response => response.data));
    };

    fetchCharacter();
  }, [id]);

  const handleFilmClick = (id) => {
    navigate(`/about-film/${id}`);
  };

  if (!character) {
    return <div>Loading...</div>;
  }

  return (
    <div className='box'>
        <div className='character'>
        <h1 id = "heroName">{character.name}</h1>
        <p className="label">Height:</p>
  <p>{character.height}</p>

  <p className="label">Mass:</p>
  <p>{character.mass}</p>

  <p className="label">Hair color:</p>
  <p>{character.hair_color}</p>

  <p className="label">Skin color:</p>
  <p>{character.skin_color}</p>

  <p className="label">Eyes color:</p>
  <p>{character.eye_color}</p>

  <p className="label">Birth Year:</p>
  <p>{character.birth_year}</p>

  <p className="label">Gender:</p>
  <p>{character.gender}</p>
        <div className="filmInclude">
            {films.map((film, index) => (
                <div key={index} onClick={() => handleFilmClick(film.episode_id)}>
                    <p>{film.title}</p>
                </div>
            ))}
        </div>
        </div>
    </div>
  );
};

export default Hero;
