import {
  fetchHeroDetailsStart,
  fetchHeroDetailsSuccess,
  fetchHeroDetailsError
} from '../store/heroActions';
import { useParams, navigate } from 'react-router-dom';

import { useDispatch } from 'react-redux';
import { useEffect } from 'react';

const Hero = () => {
  const dispatch = useDispatch();
  const { id } = useParams();

  useEffect(() => {
    const fetchHeroDetails = async () => {
      dispatch(fetchHeroDetailsStart(id));

      try {
        const response = await fetch(`https://swapi.dev/api/people/${id}/`);
        if (!response.ok) {
          throw new Error(`HTTP error! Status: ${response.status}`);
        }

        const heroData = await response.json();

        const filmData = await Promise.all(heroData.films.map(async (url) => {
          const filmResponse = await fetch(url);
          if (!filmResponse.ok) {
            throw new Error(`HTTP error! Status: ${filmResponse.status}`);
          }

          return filmResponse.json();
        }));

        dispatch(fetchHeroDetailsSuccess({ hero: heroData, films: filmData }));
      } catch (error) {
        dispatch(fetchHeroDetailsError(error));
