import axios from 'axios';

// Дії для обробки деталей фільму
export const FETCH_FILM_DETAILS_START = 'FETCH_FILM_DETAILS_START';
export const FETCH_FILM_DETAILS_SUCCESS = 'FETCH_FILM_DETAILS_SUCCESS';
export const FETCH_FILM_DETAILS_ERROR = 'FETCH_FILM_DETAILS_ERROR';
export const SET_LOADING = 'SET_LOADING';

export const setLoading = (loading) => ({
  type: SET_LOADING,
  payload: loading,
});

export const fetchFilmDetailsStart = (filmId) => ({
  type: FETCH_FILM_DETAILS_START,
  payload: filmId,
});

export const fetchFilmDetailsSuccess = (filmDetails) => ({
  type: FETCH_FILM_DETAILS_SUCCESS,
  payload: filmDetails,
});

export const fetchFilmDetailsError = (error) => ({
  type: FETCH_FILM_DETAILS_ERROR,
  payload: error,
});

export const fetchFilmDetails = (filmId) => async (dispatch) => {
  try {
    dispatch(fetchFilmDetailsStart(filmId));
    dispatch(setLoading(true));

    const response = await axios.get(`https://swapi.dev/api/films/${filmId}/`);
    const filmData = response.data;

    const charactersData = await Promise.all(filmData.characters.map(url => axios.get(url).then(res => res.data)));
    const planetsData = await Promise.all(filmData.planets.map(url => axios.get(url).then(res => res.data)));
    const starshipsData = await Promise.all(filmData.starships.map(url => axios.get(url).then(res => res.data)));
    const vehiclesData = await Promise.all(filmData.vehicles.map(url => axios.get(url).then(res => res.data)));
    const speciesData = await Promise.all(filmData.species.map(url => axios.get(url).then(res => res.data)));

    const filmDetails = {
      film: filmData,
      characters: charactersData,
      planets: planetsData,
      starships: starshipsData,
      vehicles: vehiclesData,
      species: speciesData,
    };

    dispatch(fetchFilmDetailsSuccess(filmDetails));
    dispatch(setLoading(false));
  } catch (error) {
    dispatch(fetchFilmDetailsError(error.message));
    dispatch(setLoading(false));
  }
};
