import React, { useEffect } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { useLocation } from 'react-router-dom';
import { fetchFilmDetails, setLoading } from '../store/aboutFilmActions';
import { useNavigate } from 'react-router-dom';

import star from '../img/star.jpeg';

const AboutFilm = () => {
  const location = useLocation();
  const dispatch = useDispatch();
  const navigate = useNavigate();
  const films = useSelector((state) => state.films.films);

  const { filmIndex } = location.state || {};
  const filmFromLocation = filmIndex !== undefined && films.length > 0 ? films[filmIndex] : null;

  // Отримати episode_id з об'єкта фільму
  const episode_id = filmFromLocation ? filmFromLocation.url : null;
  const filmIdFromUrl = episode_id ? episode_id.split('/').slice(-2, -1)[0] : null;

  useEffect(() => {
    console.log('1. useEffect - Fetching film details');
    if (filmIdFromUrl) {
      console.log('2. Inside if statement - filmIdFromUrl:', filmIdFromUrl);
      dispatch(setLoading(true));
      dispatch(fetchFilmDetails(filmIdFromUrl))
        .then(() => {
          console.log('3. Fetching film details successful');
          dispatch(setLoading(false));
        })
        .catch((error) => {
          console.error('3. Fetching film details error:', error);
          dispatch(setLoading(false));
        });
    }
  }, [filmIdFromUrl, dispatch]);

  const filmDetails = useSelector((state) => state.aboutFilm);

  if (!filmDetails.film || filmDetails.loading) {
    console.log('4. Loading film details...');
    return <p>Loading...</p>;
  }

  if (!filmDetails.film) {
    console.log('5. No film details available.');
    return <p>No data available.</p>;
  }

  const { film, planets, starships, vehicles, species, characters } = filmDetails;

  const navigateToCharacter = (url) => {
    const characterId = url.split('/')[5];
    navigate(`/character/${characterId}`);
  };

  console.log('6. Rendering film details:', filmDetails);

  return (
    <div className='block'>
      <img src={star} alt='star' className='backStar' />
      <div className='filmInfo'>
        <p id="mainTitle"><span id='tekst'>{film.title}</span></p>
        {/* ... (решта вашого коду) */}
      </div>
    </div>
  );
}

export default AboutFilm;
