// heroActions.js
export const fetchHeroDetailsStart = (id) => ({
    type: 'FETCH_HERO_DETAILS_START',
    payload: id,
  });

  export const fetchHeroDetailsSuccess = (data) => ({
    type: 'FETCH_HERO_DETAILS_SUCCESS',
    payload: data,
  });

  export const fetchHeroDetailsError = (error) => ({
    type: 'FETCH_HERO_DETAILS_ERROR',
    payload: error,
  });
