// aboutFilmReducer.js

import axios from 'axios';

// Action types
const SET_FILM_DETAILS = 'SET_FILM_DETAILS';

// Action creator
export const setFilmDetails = (film) => ({
  type: SET_FILM_DETAILS,
  payload: film,
});

// Initial state
const initialState = {
  film: null,
  filmCharacters: null,
  filmPlanets: null,
  filmSpecies: null,
  filmStarships: null,
  filmVehicles: null,
};

// Reducer
const aboutFilmReducer = (state = initialState, action) => {
  switch (action.type) {
    case SET_FILM_DETAILS:
      return {
        ...state,
        film: action.payload,
        filmCharacters: action.payload.characters,
        filmPlanets: action.payload.planets,
        filmSpecies: action.payload.species,
        filmStarships: action.payload.starships,
        filmVehicles: action.payload.vehicles,
      };
    default:
      return state;
  }
};

export default aboutFilmReducer;
