// AboutFilmReducer.js
const initialState = {
    filmDetails: null,
    characters: [], // Додайте інші дані за потреби
    planets: [],
    starships: [],
    vehicles: [],
    species: [],
    loading: false,
  };

  const aboutFilmReducer = (state = initialState, action) => {
    switch (action.type) {
      case RESET_FILM_DETAILS:
        return {
          ...state,
          filmDetails: null,
          characters: [],
          // інші поля з об'єкта aboutFilmState
      };

      case 'SET_FILM_DETAILS':
        return {
          ...state,
          filmDetails: action.payload,
        };
      case 'SET_CHARACTERS':
        return {
          ...state,
          characters: action.payload,
        };
      case 'SET_CHARACTER_NAMES':
        return {
          ...state,
          characterNames: action.payload,
        };
      case 'SET_PLANETS':
        return {
          ...state,
          planets: action.payload,
        };
      case 'SET_STARSHIPS':
        return {
          ...state,
          starships: action.payload,
        };
      case 'SET_VEHICLES':
        return {
          ...state,
          vehicles: action.payload,
        };
      case 'SET_SPECIES':
        return {
          ...state,
          species: action.payload,
        };

      // Додайте інші case для інших типів даних
      default:
        return state;
    }

  };

  export default aboutFilmReducer;
