import React from 'react';
import axios from 'axios';

const swapiUrl = 'https://swapi.dev/api/films/';

class Films extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      episode: '',
      directorsName: '',
      releaseDate: ''
    };
  }

  componentDidMount() {
    axios.get(swapiUrl)
      .then((res) => {
        const film = res.data.results[0]; // Assuming you want details for the first film
        this.setState({
          episode: film.episode_id,
          directorsName: film.director,
          releaseDate: film.release_date
        });
      })
      .catch((error) => {
        console.error('Error fetching data:', error);
      });
  }

  render() {
    return (
      <div className='film'>
        <h1>Star Wars Films</h1>

            Episode {this.state.episode}: {this.state.directorsName}
            <p>Director: {this.state.directorsName}</p>
            <p>Release Date: {this.state.releaseDate}</p>

      </div>
    );
  }
}

export default Films;
