// aboutFilmReducer.js

// ...

const SET_FILM_DETAILS = 'SET_FILM_DETAILS';
const SET_LOADING = 'SET_LOADING';

export const setFilmDetails = (filmDetails) => ({
  type: SET_FILM_DETAILS,
  payload: filmDetails,
});

export const setLoading = (loading) => ({
  type: SET_LOADING,
  payload: loading,
});

const initialState = {
  film: null,
  characters: [],
  planets: [],
  starships: [],
  vehicles: [],
  species: [],
  loading: false, // Додали статус завантаження
};

const aboutFilmReducer = (state = initialState, action) => {
  switch (action.type) {
    case SET_FILM_DETAILS:
      return {
        ...state,
        ...action.payload,
        loading: false, // Встановлюємо статус завантаження в false після завантаження даних
      };
    case SET_LOADING:
      return {
        ...state,
        loading: action.payload,
      };
    default:
      return state;
  }
};

export default aboutFilmReducer;
