import React, { useEffect, useState } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import axios from 'axios';

function Hero() {
  const { id } = useParams();
  const navigate = useNavigate();
  const [character, setCharacter] = useState(null);
  const [films, setFilms] = useState([]);

  useEffect(() => {
    const fetchCharacter = async () => {
      const data = await axios.get(`https://swapi.dev/api/people/${id}/`);
      setCharacter(data.data);

      const filmData = await Promise.all(data.data.films.map(film => axios.get(film)));
      setFilms(filmData.map(response => response.data));
    };

    fetchCharacter();
  }, [id]);

  const handleFilmClick = (filmId) => {
    // Використовуйте navigate для переходу на сторінку AboutFilm
    navigate(`/about-film/${filmId}`);
  };

  if (!character) {
    return <div>Loading...</div>;
  }

  return (
    <div className='box'>
      <div className='character'>
        <h1>{character.name}</h1>
        {/* Інші властивості героя */}

        <div className="filmInclude">
          {films.map((film, index) => (
            <div key={index}>
              {/* Використовуйте onClick для обробки кліку на фільм */}
              <h2 onClick={() => handleFilmClick(film.episode_id)}>{film.title}</h2>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
}

export default Hero;
