// Action types
import { SET_HERO_DETAILS } from './heroActions';

// Initial state
const initialState = {
  hero: null,
  films: [],
};

// Reducer
const heroReducer = (state = initialState, action) => {
  switch (action.type) {
    case SET_HERO_DETAILS:
      return {
        ...state,
        ...action.payload,
      };
    default:
      return state;
  }
};

export default heroReducer;
