// AboutFilmReducer.js
import { SET_FILM_DETAILS, SET_CHARACTERS, SET_PLANETS } from './AboutFilmAction';

const initialState = {
  filmDetails: {},
  characters: [],
  planets: [],

  // інші початкові дані, які вам можуть знадобитися
};

const aboutFilmReducer = (state = initialState, action) => {
  switch (action.type) {
    case SET_FILM_DETAILS:
      return {
        ...state,
        filmDetails: action.payload,
      };
    case SET_CHARACTERS:
      return {
        ...state,
        characters: action.payload,
      };
    case SET_PLANETS:
      return {
        ...state,
        planets: action.payload,
      };
    // інші випадки для інших даних
    default:
      return state;
  }
};

export default aboutFilmReducer;
